import React from 'react';
import './App.css';
import { FaGithub, FaTwitter, FaReact } from "react-icons/fa";
import { Timeline, Follow } from 'react-twitter-widgets'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
  },
}));

function App() {
  const classes = useStyles();

  return (
    <div className="App">
      <Grid container spacing={3}>
        <Grid item xs={8}>
          <Paper className={classes.paper}>
          <h1>Tennis Tweets  <FaTwitter /> </h1> 
          <br></br>

      <h2> What is it? </h2>
      <p>Tennis Tweets is a Twitter bot that live tweets ATP & ITF tennis scores.
        I created this because <br></br>I am a tennis fan and I wanted to learn more about internet bots and web scraping. </p>
      <h2>What was it built with?</h2>
      <p>Built with Python and JavaScript (React) using libraries Tweepy and BeautifulSoup.</p>
      <h2>Total Statistics</h2>
      <Grid container spacing={1}>
      <Grid item xs={1}>
          <Paper className={classes.paper}><h4>Tweets</h4></Paper>
        </Grid>
        <Grid item xs={1}>
          <Paper className={classes.paper}><h4>Tweet Impressions</h4></Paper>
        </Grid>
        <Grid item xs={1}>
          <Paper className={classes.paper}><h4>Followers</h4></Paper>
        </Grid>
        <Grid item xs={1}>
          <Paper className={classes.paper}><h4>Profile Visits</h4></Paper>
        </Grid>
        </Grid>
          </Paper>
        </Grid>
        <Grid item xs={4}>
          <Paper className={classes.paper}>
          <Timeline
        dataSource={{
          sourceType: 'profile',
          screenName: 'tennisscores2'
        }}
        options={{
          username: 'Tennis_Tweets',
          height: '550',
          width: '300'
        }}
        onLoad={() => console.log('Timeline is loaded!')}
      />
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
